Rule.Source:
  import java.util.Calendar;
import java.util.Date;
import sailpoint.object.Identity;


Calendar currentDate = Calendar.getInstance();
currentDate.add(Calendar.MONTH, 1);





Date formDate = identityModel.get("date");
if (formDate == null) {
    return "Date is not provided";
}



Calendar formDateCal = Calendar.getInstance();
formDateCal.setTime(formDate);


Calendar now = Calendar.getInstance();


if (formDateCal.after(now) && formDateCal.before(currentDate)) {
    String UserName = identityModel.get("username");
    Identity identity = context.getObjectByName(Identity.class, UserName);
    identity.setAttribute("date", identityModel.get("date"));
} else {
    return "Please select an appropriate date within one month from today.";
}
